// palindrome code using recursion. 

#include<iostream>
using namespace std;
#include<string.h>
bool palindrome(string str,int start,int end)
{
    if(start>=end)
    {
        return true;
    }
    else{
        return(str[start]==str[end])&&
        palindrome(str,start+1,end-1);
    }


}
int main()
{
    string name="naman";
    int n=name.length();
    int start=0;
    int end=n-1;
    cout<<palindrome(name,start,end);
   
}

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   